mapper.select:
SELECT * FROM CT_PIG_COLLECTIONRECORD c left join T_BD_PERSON p on p.FID = c.CFCOLLECTORID
c.CFORGUNITID = #{orgId,jdbcType=VARCHAR} AND
c.CFEARNUMBERID LIKE #{pattern} or p.FNAME_L2 LIKE #{pattern}

SELECT * FROM CT_PIG_COLLECTIONRECORD c WHERE c.CFORGUNITID = #{storageId,jdbcType=VARCHAR}
AND c.CFCOLLECTIONTIME <= #{endDate} AND c.CFCOLLECTIONTIME >= #{startDate}
ORDER BY c.CFCOLLECTIONTIME
